import React, { Component, useImperativeHandle } from "react";

class PhoneForm extends Component {
	state = {
		name: "",
	};

	handleChange = (e) => {
		this.setState({
			name: e.target.value,
		});
	};

	render() {
		return (
			<form>
				<input onChange={this.handleChange} value={this.state.name} />
			</form>
		);
	}
}

export default PhoneForm;
