import React, { Component } from 'react';

class PhoneForm extends Component {
  state = {
    id: '',
    pw: '',
  };

  handleChange = (e) => {
    this.setState({
      name: e.target.value,
    });
  };

  render() {
    const { id, pw } = this.state;

    return (
      <form>
        <input placeholder="id" onChange={this.handleChange} value={id} />
        <input placeholder="pw" value={pw} />
      </form>
    );
  }
}

export default PhoneForm;
