import React, { Component } from 'react';

class PhoneForm extends Component {
  state = {
    id: '',
    pw: '',
  };

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  render() {
    const { id, pw } = this.state;

    return (
      <form>
        <input
          name="id"
          placeholder="id"
          onChange={this.handleChange}
          value={id}
        />
        <input
          name="pw"
          placeholder="pw"
          onChange={this.handleChange}
          value={pw}
        />
        <div>
          {id}
          {pw}
        </div>
      </form>
    );
  }
}

export default PhoneForm;
