import React, { Component, useImperativeHandle } from "react";

class PhoneForm extends Component {
	state = {
		name: "",
	};

	handleChange = (e) => {
		this.setState({
			name: e.target.value,
		});
	};

	render() {
		const { name } = this.state;

		return (
			<form>
				<input onChange={this.handleChange} value={name} />
				{name}
			</form>
		);
	}
}

export default PhoneForm;
